CKEDITOR.plugins.add( 'simplebox', {
    // Simple Box widget code.
    requires: 'widget',
    init: function( editor ) {
        // Plugin logic goes here...
        console.log('simple box init')
        var pluginDirectory = this.path;
        editor.addContentsCss( pluginDirectory + 'styles/simplebox.css' );

        editor.widgets.add( 'simplebox', {
            // Widget code.
            template:
            '<div class="simplebox align-left" style="width:250px">' +
                '<h2 class="simplebox-title">Title</h2>' +
                '<div class="simplebox-content"><p>Content...</p></div>' +
            '</div>',

            editables: {
                title: {
                    selector: '.simplebox-title',
                    allowedContent: 'br strong em'
                },
                content: {
                    selector: '.simplebox-content',
                    allowedContent: 'p br ul ol li strong em'
                }
            },

            allowedContent: 'div(!simplebox,align-left,align-right,align-center){width}; div(!simplebox-content); h2(!simplebox-title)',
            requiredContent: 'div(simplebox)',
            upcast: function( element ) {
                return element.name == 'div' && element.hasClass( 'simplebox' );
            }
        } );
    }
} );