CKEDITOR.plugins.add( 'timestamp', {
    onLoad: function() {
        CKEDITOR.addCss(        // Use this approach if the styling of elements is needed only insie editor
            'em {' +
                'border: solid 2px green' +
            '}'
        );
    },

    init: function( editor ) {
        //Plugin logic goes here.
        console.log('timestamp init')

        var pluginDirectory = this.path;
        editor.addContentsCss( pluginDirectory + 'styles/timestamp.css' );  // Use this approach if styling is need for content in classic editor. To apply same styling to content generated by this classic editor, use this style sheet in the web page where this content is finally displayed. Inline editor will not be able to pick this style sheet if given like this.

        editor.addCommand( 'insertTimestamp', {
            exec: function( editor ) {
                var now = new Date();
                editor.insertHtml( 'The current date and time is: <em>' + now.toString() + '</em>' );
            }
        });
    }
});